def on_on_overlap(sprite, otherSprite):
    sprite.destroy()
    scene.camera_shake(4, 500)
    info.change_life_by(-1)
sprites.on_overlap(SpriteKind.enemy, SpriteKind.player, on_on_overlap)

def on_on_score():
    game.game_over(True)
    game.set_game_over_message(True, "Win!!!")
info.on_score(500, on_on_score)

def on_on_overlap2(sprite2, otherSprite2):
    otherSprite2.destroy(effects.ashes, 300)
    info.change_score_by(1)
    game.set_game_over_scoring_type(game.ScoringType.HIGH_SCORE)
sprites.on_overlap(SpriteKind.projectile, SpriteKind.enemy, on_on_overlap2)

